import{_ as r,o as t,c as a,a2 as o}from"./chunks/framework.4MUDd9zK.js";const g=JSON.parse('{"title":"About FUNDAL","description":"","frontmatter":{"title":"About FUNDAL"},"headers":[],"relativePath":"guide/index.md","filePath":"guide/index.md"}'),i={name:"guide/index.md"};function n(s,e,l,d,c,u){return t(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="about-fundal" tabindex="-1">About FUNDAL <a class="header-anchor" href="#about-fundal" aria-label="Permalink to &quot;About FUNDAL&quot;">​</a></h1><p><strong>FUNDAL</strong> (Fortran UNified Device Acceleration Library) is a pure Fortran library that provides a unified API for managing accelerated device memory using either OpenACC or OpenMP offloading.</p><p>OpenACC and OpenMP both offer runtime routines for device memory management (allocation, copy, free), but they expose different C-pointer-based interfaces. FUNDAL wraps these routines behind a single Fortran API, eliminating the need for conditional compilation macros in user code and making it straightforward to write programs that are portable across GPU vendors and compiler toolchains.</p><h2 id="design-goals" tabindex="-1">Design goals <a class="header-anchor" href="#design-goals" aria-label="Permalink to &quot;Design goals&quot;">​</a></h2><ul><li><strong>KISS</strong> — Keep It Simple and Stupid: minimal API surface, no unnecessary abstractions</li><li><strong>Unified</strong> — one set of calls works for both OpenACC and OpenMP backends</li><li><strong>Seamless</strong> — OpenACC and OpenMP pragmas can coexist in the same source; unrecognised directives are safely ignored by the compiler</li><li><strong>Pure Fortran</strong> — no C code in the library itself; C interoperability is used internally for runtime calls only</li></ul><h2 id="authors" tabindex="-1">Authors <a class="header-anchor" href="#authors" aria-label="Permalink to &quot;Authors&quot;">​</a></h2><ul><li>Stefano Zaghi — <a href="mailto:stefano.zaghi@cnr.it" target="_blank" rel="noreferrer">stefano.zaghi@cnr.it</a></li><li>Giacomo Rossi — <a href="mailto:giacomo.rossi@intel.com" target="_blank" rel="noreferrer">giacomo.rossi@intel.com</a></li><li>Andrea di Mascio — <a href="mailto:andrea.dimascio@univaq.it" target="_blank" rel="noreferrer">andrea.dimascio@univaq.it</a></li><li>Francesco Salvadore — <a href="mailto:f.salvadore@cineca.it" target="_blank" rel="noreferrer">f.salvadore@cineca.it</a></li></ul><h2 id="copyrights" tabindex="-1">Copyrights <a class="header-anchor" href="#copyrights" aria-label="Permalink to &quot;Copyrights&quot;">​</a></h2><p>FUNDAL is distributed under a multi-licensing system:</p><table tabindex="0"><thead><tr><th>Use case</th><th>License</th></tr></thead><tbody><tr><td>FOSS projects</td><td><a href="http://www.gnu.org/licenses/gpl-3.0.html" target="_blank" rel="noreferrer">GPL v3</a></td></tr><tr><td>Closed source / commercial</td><td><a href="http://opensource.org/licenses/BSD-2-Clause" target="_blank" rel="noreferrer">BSD 2-Clause</a></td></tr><tr><td>Closed source / commercial</td><td><a href="http://opensource.org/licenses/BSD-3-Clause" target="_blank" rel="noreferrer">BSD 3-Clause</a></td></tr><tr><td>Closed source / commercial</td><td><a href="http://opensource.org/licenses/MIT" target="_blank" rel="noreferrer">MIT</a></td></tr></tbody></table><blockquote><p>Anyone interested in using, developing, or contributing to FUNDAL is welcome — pick the license that best fits your needs.</p></blockquote>',11)])])}const p=r(i,[["render",n]]);export{g as __pageData,p as default};
